sequence.script:

				var leadIds = mc.getProperty('uri.var.leadIds');
                var query="";
                var added=0;
                var ids=leadIds.split(',');
                if(ids.length==1){
                    query="leadId="+ids[0];
                }
                else{
                    query="leadIds="+ids[0];
                    var idList;
                    for(var i = 1; i < ids.length; i++){
                        idList=","+ids[i];
                    }
                    query+=idList
                }
                mc.setProperty('uri.var.query', query);
